let desiredAngle = 0;
let dx = 0;
let dy = 0;
if (perspective.keysDown.has('ArrowUp')) {
  delta.drive = 1.0;
  dy = -1
}
if (perspective.keysDown.has('ArrowDown')) {
  delta.drive = 1.0;
  dy = 1
}

if (perspective.keysDown.has('ArrowRight')) {
  delta.drive = 1.0;
  dx = -1
}
if (perspective.keysDown.has('ArrowLeft')) {
  delta.drive = 1.0;
  dx = 1
}

if (dx != 0 || dy != 0) {
  desiredAngle = Math.atan2(dx, dy);
}

delta.turn = desiredAngle - perspective.currentHeading;


if (delta.turn > Math.PI) {
  delta.turn -= 2 * Math.PI;
}
if (delta.turn < -Math.PI) {
  delta.turn += 2 * Math.PI;
}
